      --muted: rgba(255,255,255,0.62);
      --hairline: rgba(255,255,255,0.10);
      --accent: #8dd6ff;
      --accent-2: #c5a7ff;
      --shadow: 0 24px 60px rgba(0,0,0,0.45);
      --radius: 16px;
      --max: 1080px;
    }

    *{ box-sizing:border-box; }
    html, body{ height:100%; }
    body{
      margin:0;
      font-family: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, "Apple Color Emoji","Segoe UI Emoji";
      color: var(--text);
      background: radial-gradient(1200px 700px at 20% 10%, rgba(141,214,255,0.18), transparent 60%),
                  radial-gradient(900px 600px at 85% 25%, rgba(197,167,255,0.18), transparent 55%),
                  radial-gradient(900px 600px at 50% 100%, rgba(255,255,255,0.06), transparent 55%),
                  var(--bg);
      letter-spacing: -0.01em;
    }

    a{ color:inherit; text-decoration:none; }

    .wrap{
      max-width: var(--max);
      margin: 0 auto;
      padding: 28px 22px 44px;
    }

    header{
      display:flex;
      align-items:center;
      justify-content:space-between;
      gap:16px;
      padding: 10px 0 22px;
    }

    .brand{
      display:flex;
      align-items:center;
      gap:12px;
      min-width: 240px;
    }

    .mark{
      width: 34px;
      height: 34px;
      border-radius: 10px;
      background:
        radial-gradient(12px 12px at 30% 30%, rgba(255,255,255,0.9), rgba(255,255,255,0) 60%),
        linear-gradient(135deg, rgba(141,214,255,0.9), rgba(197,167,255,0.85));
      box-shadow: 0 12px 26px rgba(0,0,0,0.28);
    }

    .brand strong{
      font-weight: 650;
      letter-spacing: -0.02em;
    }

    nav{
      display:flex;
      align-items:center;
      gap:18px;
      color: var(--muted);
      font-size: 14px;
    }

    nav a{
      padding: 8px 10px;
      border-radius: 10px;
      transition: background 160ms ease, color 160ms ease;
    }

    nav a:hover{
      background: rgba(255,255,255,0.06);
      color: rgba(255,255,255,0.82);
    }

    .cta{
      display:flex;
      align-items:center;
      gap:10px;
    }

    .btn{
      display:inline-flex;
      align-items:center;
      justify-content:center;
      gap:10px;
      padding: 10px 14px;
      border-radius: 12px;
      border: 1px solid var(--hairline);
      background: rgba(255,255,255,0.04);
      color: rgba(255,255,255,0.84);
      font-size: 14px;
      font-weight: 560;
      transition: transform 160ms ease, background 160ms ease, border-color 160ms ease;
      cursor: pointer;
      user-select: none;
    }

    .btn:hover{
      background: rgba(255,255,255,0.07);
      border-color: rgba(255,255,255,0.16);
      transform: translateY(-1px);
    }

    .btn.primary{
      border: 1px solid rgba(141,214,255,0.25);
      background: linear-gradient(135deg, rgba(141,214,255,0.18), rgba(197,167,255,0.16));
    }

