      content:"";
      position:absolute;
      inset:-2px;
      background:
        radial-gradient(600px 260px at 25% 20%, rgba(141,214,255,0.20), transparent 60%),
        radial-gradient(520px 260px at 78% 35%, rgba(197,167,255,0.16), transparent 60%);
      opacity: 0.8;
      pointer-events:none;
    }

