      margin: 0 0 12px;
      font-size: clamp(2.1rem, 4vw, 3.2rem);
      line-height: 1.06;
      letter-spacing: -0.04em;
    }

    .sub{
      margin: 0 0 22px;
      color: var(--muted);
      font-size: 16px;
      line-height: 1.55;
      max-width: 56ch;
    }

    .actions{
      display:flex;
      flex-wrap: wrap;
      gap: 12px;
      margin-top: 14px;
      margin-bottom: 10px;
    }

    .note{
      margin: 8px 0 0;
      color: rgba(255,255,255,0.50);
      font-size: 13px;
    }

    .side{
      border-left: 1px solid rgba(255,255,255,0.08);
      padding-left: 26px;
      margin-left: 6px;
    }

    .pill{
      display:inline-flex;
      gap:8px;
      align-items:center;
      padding: 8px 10px;
      border-radius: 999px;
      border: 1px solid rgba(255,255,255,0.10);
      background: rgba(0,0,0,0.18);
      color: rgba(255,255,255,0.70);
      font-size: 13px;
